package board;import agent.Agent;public class Cell {	private Type type;	private int x, y;	private Agent agent;	private float temperature = 0.0f, coConcentration = 0.0f;	/**	 * Typ komórki.	 * 	 * Raczej nie u¿ywajmy na razie {@link #EMPTY}, bo nie wiem co to by mog³o	 * znaczyæ... Dlatego zrobi³em typ, a nie flagê blocked, bo nie wiadomo czy	 * siê jeszcze nie przyda, a pewnie tak!	 * 	 * @author Michal	 * 	 */	public enum Type {		EMPTY, BLOCKED	}	public Cell(int x, int y) {		type = Type.EMPTY;		this.x = x;		this.y = y;	}	public void setType(Type type) {		this.type = type;	}	public Type getType() {		return type;	}	public int getX() {		return x;	}	public int getY() {		return y;	}	public float getTemperature() {		return temperature;	}	public float getCOConcentration() {		return coConcentration;	}	public void removeAgent() {		agent = null;	}	public void addAgent(Agent agent) {		this.agent = agent;	}	public Agent getAgent() {		return agent;	}}